.three-lines {
  display: inline-block;
  width: 15px;
  cursor: pointer;
  transition: transit( padding );

  @include bp( 1 ) {
    padding: 18px 16px 18px 8px;
  }

  @include bp( 1024 ) {
    padding: 36px 16px 36px 8px;
  }

  &:hover > div {
    background-color: $lbrown;
  }

  .line {
    display: block;
    height: 2px;
    background-color: $blue;
    margin-bottom: 3px;
    transform-origin: 0 50%;

    &.l {
      &-1,
      &-2,
      &-3 {
        transition: transit( all );
      }

      &-3 {
        margin-bottom: 0;
      }
    }
  }

  &:hover {
    .l {
      &-1 {
        transform: rotate( 42.5deg );
      }

      &-2 {
        visibility: hidden;
      }

      &-3 {
        transform: rotate( -42.5deg );
      }
    }
  }
}

