",".three-lines {
  display: inline-block;
  width: 15px;
  padding: 18px 16px;
  cursor: pointer;

  .line {
    display: block;
    height: 2px;
    background-color: black;
    margin-bottom: 3px;
    transform-origin: 0 50%;

    &.l {
      &-1,
      &-2,
      &-3 {
        transition: transit( all );
      }

      &-3 {
        margin-bottom: 0;
      }
    }
  }

  &:hover {
    .l {
      &-1 {
        transform: rotate( 45deg );
      }

      &-2 {
        visibility: hidden;
      }

      &-3 {
        transform: rotate( -45deg );
      }
    }
  }
}
","a {
  text-decoration: none;
  color: $blue;
}
