",".main-navigation {
  float: left;
  width: 100%;

  &-wrapper {
    @include center-block;

    max-width: 1190px;
  }

  .custom-logo-link,
  .nav-primary,
  .social-menu,
  .search-form,
  .three-lines {
    display: inline-block;
  }
}

.side {
  &-left {
    float: left;
  }

  &-right {
    float: right;
  }
}

.nav-primary {
  ul {
    padding-left: 0;

    ul {
      display: none;
    }

    li:hover > ul {
      display: block;
      position: absolute;
      left: 0;
      width: 200px;

      li {
        display: block;

        &:hover > ul {
          top: 0;
          left: 100%;
        }
      }
    }
  }

  li {
    display: inline;
    position: relative;
  }
}
","// Rem output with px fallback
@mixin font-size( $sizeValue: 1 ) {
  font-size: ( $sizeValue * 16 ) * 1px;
  font-size: $sizeValue * 1rem;
}

// Center block
@mixin center-block {
  display: block;
  margin-left: auto;
  margin-right: auto;
}

// Clearfix
@mixin clearfix(  ) {
  content: '';
  display: table;
  table-layout: fixed;
}

// Clear after ( not all clearfix need this also )
@mixin clearfix-after(  ) {
  clear: both;
}

// Column width with margin
@mixin column-width( $numberColumns: 3 ) {
  width: map-get( $columns, $numberColumns ) - ( ( $columns__margin * ( $numberColumns - 1 ) ) / $numberColumns );
}

