  @if ( $q != null ) {
    @if ( $orientation == null ) {
      @media ( min-#{$orient}: $p * 1px ) and ( max-#{$orient}: $q * 1px ) {
        @content;
      }
    }

    @else {
      @media ( min-#{$orient}: $p * 1px ) and ( max-#{$orient}: $q * 1px ) and ( orientation: $orientation ) {
        @content;
      }
    }
  }

  @else {
    @if ( $orientation == null ) {
      @media ( min-#{$orient}: $p * 1px ) {
        @content;
      }
    }

    @else {
      @media ( min-#{$orient}: $p * 1px ) and ( orientation: $orientation ) {
        @content;
      }
    }
