}

@mixin colored-links( $link, $hover: $link, $visited: $hover ) {
  color: $link; //!important;
  transition: transit( color ), transit( border-bottom, 0.5 );

  &:visited {
    color: $visited; //!important;
  }

  &:hover {
    color: $hover; //!important;
  }

