  position: absolute;

  @if ( $direction == 'b' ) {
    top: 50%;
    left: 50%;
    width: 100%;
    text-align: center;
    transform: translate( -50%, -50% );
  }

  @else if ( $direction == 'v' ) {
    top: 50%;
    transform: translateY( -50% );
  }

  @else if ( $direction == 'h' ) {
    left: 50%;
    transform: translateX( -50% );
  }
}

@mixin do_gradient( $orientation: 'lr', $color: 'wt' ) {
  @if ( $color == 'wt' ) {
